mapper.select:
SELECT * FROM shop


	OR user_id = #{orEq.userId}
	OR user_id LIKE concat('%', #{orLike.userId}, '%')
	AND user_id = #{andEq.userId}
	AND user_id LIKE concat('%', #{andLike.userId}, '%')

	OR name = #{orEq.name}
	OR name LIKE concat('%', #{orLike.name}, '%')
	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR status = #{orEq.status}
	OR status LIKE concat('%', #{orLike.status}, '%')
	AND status = #{andEq.status}
	AND status LIKE concat('%', #{andLike.status}, '%')

	OR deleted = #{orEq.deleted}
	OR deleted LIKE concat('%', #{orLike.deleted}, '%')
	AND deleted = #{andEq.deleted}
	AND deleted LIKE concat('%', #{andLike.deleted}, '%')

	OR create_date = #{orEq.createDate}
	OR create_date LIKE concat('%', #{orLike.createDate}, '%')
	AND create_date = #{andEq.createDate}
	AND create_date LIKE concat('%', #{andLike.createDate}, '%')

	OR description = #{orEq.description}
	OR description LIKE concat('%', #{orLike.description}, '%')
	AND description = #{andEq.description}
	AND description LIKE concat('%', #{andLike.description}, '%')

	OR contact = #{orEq.contact}
	OR contact LIKE concat('%', #{orLike.contact}, '%')
	AND contact = #{andEq.contact}
	AND contact LIKE concat('%', #{andLike.contact}, '%')

	OR zip_code = #{orEq.zipCode}
	OR zip_code LIKE concat('%', #{orLike.zipCode}, '%')
	AND zip_code = #{andEq.zipCode}
	AND zip_code LIKE concat('%', #{andLike.zipCode}, '%')

	OR telephone = #{orEq.telephone}
	OR telephone LIKE concat('%', #{orLike.telephone}, '%')
	AND telephone = #{andEq.telephone}
	AND telephone LIKE concat('%', #{andLike.telephone}, '%')

	OR phone = #{orEq.phone}
