	AND phone = #{andEq.phone}
	AND phone LIKE concat('%', #{andLike.phone}, '%')

	OR fax = #{orEq.fax}
	OR fax LIKE concat('%', #{orLike.fax}, '%')
	AND fax = #{andEq.fax}
	AND fax LIKE concat('%', #{andLike.fax}, '%')

	OR email = #{orEq.email}
	OR email LIKE concat('%', #{orLike.email}, '%')
	AND email = #{andEq.email}
	AND email LIKE concat('%', #{andLike.email}, '%')

	OR address = #{orEq.address}
	OR address LIKE concat('%', #{orLike.address}, '%')
	AND address = #{andEq.address}
	AND address LIKE concat('%', #{andLike.address}, '%')

	AND deleted = false


	LIMIT #{limitStartRow}, #{limitEndRow}


	ORDER BY 

		${it},

SELECT * FROM shop

	deleted = false

