mapper.select:
SELECT * FROM user


	OR old_id = #{orEq.oldId}
	OR old_id LIKE concat('%', #{orLike.oldId}, '%')
	AND old_id = #{andEq.oldId}
	AND old_id LIKE concat('%', #{andLike.oldId}, '%')

	OR login_id = #{orEq.loginId}
	OR login_id LIKE concat('%', #{orLike.loginId}, '%')
	AND login_id = #{andEq.loginId}
	AND login_id LIKE concat('%', #{andLike.loginId}, '%')

	OR password = #{orEq.password}
	OR password LIKE concat('%', #{orLike.password}, '%')
	AND password = #{andEq.password}
	AND password LIKE concat('%', #{andLike.password}, '%')

	OR status = #{orEq.status}
	OR status LIKE concat('%', #{orLike.status}, '%')
	AND status = #{andEq.status}
	AND status LIKE concat('%', #{andLike.status}, '%')

	OR deleted = #{orEq.deleted}
	OR deleted LIKE concat('%', #{orLike.deleted}, '%')
	AND deleted = #{andEq.deleted}
	AND deleted LIKE concat('%', #{andLike.deleted}, '%')

	OR create_date = #{orEq.createDate}
	OR create_date LIKE concat('%', #{orLike.createDate}, '%')
	AND create_date = #{andEq.createDate}
	AND create_date LIKE concat('%', #{andLike.createDate}, '%')

	OR name = #{orEq.name}
