	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR id_card = #{orEq.idCard}
	OR id_card LIKE concat('%', #{orLike.idCard}, '%')
	AND id_card = #{andEq.idCard}
	AND id_card LIKE concat('%', #{andLike.idCard}, '%')

