	OR telephone LIKE concat('%', #{orLike.telephone}, '%')
	AND telephone = #{andEq.telephone}
	AND telephone LIKE concat('%', #{andLike.telephone}, '%')

	OR area_code = #{orEq.areaCode}
	OR area_code LIKE concat('%', #{orLike.areaCode}, '%')
	AND area_code = #{andEq.areaCode}
	AND area_code LIKE concat('%', #{andLike.areaCode}, '%')

	OR address = #{orEq.address}
