	OR recent_login_time LIKE concat('%', #{orLike.recentLoginTime}, '%')
	AND recent_login_time = #{andEq.recentLoginTime}
	AND recent_login_time LIKE concat('%', #{andLike.recentLoginTime}, '%')

	OR recent_login_ip = #{orEq.recentLoginIp}
	OR recent_login_ip LIKE concat('%', #{orLike.recentLoginIp}, '%')
	AND recent_login_ip = #{andEq.recentLoginIp}
	AND recent_login_ip LIKE concat('%', #{andLike.recentLoginIp}, '%')

	AND deleted = false


	LIMIT #{limitStartRow}, #{limitEndRow}


	ORDER BY 

		${it},

SELECT * FROM user

	deleted = false

	AND old_id = #{oldId}
	AND login_id = #{loginId}
	AND password = #{password}
	AND status = #{status}
	AND deleted = #{deleted}
	AND create_date = #{createDate}
	AND name = #{name}
	AND id_card = #{idCard}
	AND zip_code = #{zipCode}
	AND phone = #{phone}
	AND telephone = #{telephone}
	AND area_code = #{areaCode}
	AND address = #{address}
	AND last_login_time = #{lastLoginTime}
	AND last_login_ip = #{lastLoginIp}
	AND recent_login_time = #{recentLoginTime}
	AND recent_login_ip = #{recentLoginIp}

LIMIT #{start_row}, #{end_row}

