mapper.select:
SELECT * FROM user_abc_info


	OR user_id = #{orEq.userId}
	OR user_id LIKE concat('%', #{orLike.userId}, '%')
	AND user_id = #{andEq.userId}
	AND user_id LIKE concat('%', #{andLike.userId}, '%')

	OR sign_status = #{orEq.signStatus}
	OR sign_status LIKE concat('%', #{orLike.signStatus}, '%')
	AND sign_status = #{andEq.signStatus}
	AND sign_status LIKE concat('%', #{andLike.signStatus}, '%')

	OR customer_no = #{orEq.customerNo}
	OR customer_no LIKE concat('%', #{orLike.customerNo}, '%')
	AND customer_no = #{andEq.customerNo}
	AND customer_no LIKE concat('%', #{andLike.customerNo}, '%')

	OR customer_name = #{orEq.customerName}
	OR customer_name LIKE concat('%', #{orLike.customerName}, '%')
	AND customer_name = #{andEq.customerName}
	AND customer_name LIKE concat('%', #{andLike.customerName}, '%')

	OR authorize_status = #{orEq.authorizeStatus}
	OR authorize_status LIKE concat('%', #{orLike.authorizeStatus}, '%')
	AND authorize_status = #{andEq.authorizeStatus}
	AND authorize_status LIKE concat('%', #{andLike.authorizeStatus}, '%')

	AND null


	LIMIT #{limitStartRow}, #{limitEndRow}


	ORDER BY 

		${it},

SELECT * FROM user_abc_info

	1=1

	AND user_id = #{userId}
	AND sign_status = #{signStatus}
	AND customer_no = #{customerNo}
	AND customer_name = #{customerName}
	AND authorize_status = #{authorizeStatus}

LIMIT #{start_row}, #{end_row}

mapper.insert:
INSERT INTO user_abc_info (user_id, sign_status, customer_no, customer_name, authorize_status)
VALUES (#{userId}
	, #{signStatus}
	, #{customerNo}
	, #{customerName}
	, #{authorizeStatus}
	)
