mapper.select:
SELECT * FROM menu


	OR parent_id = #{orEq.parentId}
	OR parent_id LIKE concat('%', #{orLike.parentId}, '%')
	AND parent_id = #{andEq.parentId}
	AND parent_id LIKE concat('%', #{andLike.parentId}, '%')

	OR menu_type_id = #{orEq.menuTypeId}
	OR menu_type_id LIKE concat('%', #{orLike.menuTypeId}, '%')
	AND menu_type_id = #{andEq.menuTypeId}
	AND menu_type_id LIKE concat('%', #{andLike.menuTypeId}, '%')

	OR menu_key = #{orEq.menuKey}
	OR menu_key LIKE concat('%', #{orLike.menuKey}, '%')
	AND menu_key = #{andEq.menuKey}
	AND menu_key LIKE concat('%', #{andLike.menuKey}, '%')

	OR name = #{orEq.name}
	OR name LIKE concat('%', #{orLike.name}, '%')
	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR uri = #{orEq.uri}
	OR uri LIKE concat('%', #{orLike.uri}, '%')
	AND uri = #{andEq.uri}
	AND uri LIKE concat('%', #{andLike.uri}, '%')

	OR status = #{orEq.status}
	OR status LIKE concat('%', #{orLike.status}, '%')
	AND status = #{andEq.status}
	AND status LIKE concat('%', #{andLike.status}, '%')

	OR create_date = #{orEq.createDate}
	OR create_date LIKE concat('%', #{orLike.createDate}, '%')
	AND create_date = #{andEq.createDate}
	AND create_date LIKE concat('%', #{andLike.createDate}, '%')


	AND status > -1




		LIMIT #{limitStartRow}, #{limitEndRow}


		ORDER BY 

			${it},

SELECT COUNT(1) FROM menu


	OR parent_id = #{orEq.parentId}
	OR parent_id LIKE concat('%', #{orLike.parentId}, '%')
	AND parent_id = #{andEq.parentId}
	AND parent_id LIKE concat('%', #{andLike.parentId}, '%')

	OR menu_type_id = #{orEq.menuTypeId}
	OR menu_type_id LIKE concat('%', #{orLike.menuTypeId}, '%')
	AND menu_type_id = #{andEq.menuTypeId}
	AND menu_type_id LIKE concat('%', #{andLike.menuTypeId}, '%')

	OR menu_key = #{orEq.menuKey}
	OR menu_key LIKE concat('%', #{orLike.menuKey}, '%')
	AND menu_key = #{andEq.menuKey}
	AND menu_key LIKE concat('%', #{andLike.menuKey}, '%')

	OR name = #{orEq.name}
	OR name LIKE concat('%', #{orLike.name}, '%')
	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR uri = #{orEq.uri}
	OR uri LIKE concat('%', #{orLike.uri}, '%')
	AND uri = #{andEq.uri}
	AND uri LIKE concat('%', #{andLike.uri}, '%')

	OR status = #{orEq.status}
	OR status LIKE concat('%', #{orLike.status}, '%')
	AND status = #{andEq.status}
	AND status LIKE concat('%', #{andLike.status}, '%')

	OR create_date = #{orEq.createDate}
	OR create_date LIKE concat('%', #{orLike.createDate}, '%')
	AND create_date = #{andEq.createDate}
	AND create_date LIKE concat('%', #{andLike.createDate}, '%')

	AND null

mapper.insert:
INSERT INTO menu (parent_id, menu_type_id, menu_key, name, uri, status, create_date)
VALUES (#{parentId}
	, #{menuTypeId}
	, #{menuKey}
	, #{name}
	, #{uri}
	, #{status}
	, #{createDate}
	)


	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE menu SET


	, parent_id = #{parentId}
	, menu_type_id = #{menuTypeId}
	, menu_key = #{menuKey}
	, name = #{name}
	, uri = #{uri}
	, status = #{status}
	, create_date = #{createDate}

WHERE id = #{id}
