mapper.insert:
INSERT INTO product (old_id, product_type_id, origin_area_id, shop_id, unit_id, hn_code, name, status, create_date, description, show_order)
VALUES (#{oldId}
	, #{productTypeId}
	, #{originAreaId}
	, #{shopId}
	, #{unitId}
	, #{hnCode}
	, #{name}
	, #{status}
	, #{createDate}
	, #{description}
	, #{showOrder}
	)


	SELECT LAST_INSERT_ID()

