	AND tree_code = #{andEq.treeCode}
	AND tree_code LIKE concat('%', #{andLike.treeCode}, '%')

	OR parent_tree_code = #{orEq.parentTreeCode}
	OR parent_tree_code LIKE concat('%', #{orLike.parentTreeCode}, '%')
	AND parent_tree_code = #{andEq.parentTreeCode}
	AND parent_tree_code LIKE concat('%', #{andLike.parentTreeCode}, '%')

	OR description = #{orEq.description}
	OR description LIKE concat('%', #{orLike.description}, '%')
	AND description = #{andEq.description}
	AND description LIKE concat('%', #{andLike.description}, '%')

	OR show_order = #{orEq.showOrder}
	OR show_order LIKE concat('%', #{orLike.showOrder}, '%')
	AND show_order = #{andEq.showOrder}
	AND show_order LIKE concat('%', #{andLike.showOrder}, '%')


	AND status > -1




		LIMIT #{limitStartRow}, #{limitEndRow}


		ORDER BY 

			${it},

SELECT COUNT(1) FROM product_type


