mapper.insert:
INSERT INTO product_type (parent_id, old_id, name, pin_yin_name, create_date, status, tree_code, parent_tree_code, description, show_order)
VALUES (#{parentId}
	, #{oldId}
	, #{name}
	, #{pinYinName}
	, #{createDate}
	, #{status}
	, #{treeCode}
	, #{parentTreeCode}
	, #{description}
	, #{showOrder}
	)


	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE product_type SET


	, parent_id = #{parentId}
	, old_id = #{oldId}
	, name = #{name}
	, pin_yin_name = #{pinYinName}
	, create_date = #{createDate}
	, status = #{status}
	, tree_code = #{treeCode}
	, parent_tree_code = #{parentTreeCode}
	, description = #{description}
	, show_order = #{showOrder}

WHERE id = #{id}
