mapper.select:
SELECT * FROM unit_type


	OR name = #{orEq.name}
	OR name LIKE concat('%', #{orLike.name}, '%')
	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR show_order = #{orEq.showOrder}
	OR show_order LIKE concat('%', #{orLike.showOrder}, '%')
	AND show_order = #{andEq.showOrder}
	AND show_order LIKE concat('%', #{andLike.showOrder}, '%')





		LIMIT #{limitStartRow}, #{limitEndRow}


		ORDER BY 

			${it},

SELECT COUNT(1) FROM unit_type


	OR name = #{orEq.name}
	OR name LIKE concat('%', #{orLike.name}, '%')
	AND name = #{andEq.name}
	AND name LIKE concat('%', #{andLike.name}, '%')

	OR show_order = #{orEq.showOrder}
	OR show_order LIKE concat('%', #{orLike.showOrder}, '%')
	AND show_order = #{andEq.showOrder}
	AND show_order LIKE concat('%', #{andLike.showOrder}, '%')

	AND null

mapper.insert:
INSERT INTO unit_type (name, show_order)
VALUES (#{name}
	, #{showOrder}
	)


	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE unit_type SET


	, name = #{name}
	, show_order = #{showOrder}

WHERE id = #{id}
